      if(data){
        data.forEach(element => {

          if(element.curative){
            arrayPotion.push(element)
          }
          else{
            arrayPoison.push(element)
          }
        });

        poison = arrayPoison[getRandom(arrayPoison.length)]
        potion = arrayPotion[getRandom(arrayPotion.length)]

        console.log(potion)
        console.log(poison)

      }


  }, [data]);

  function getRandom (length){
    let random = Math.floor(Math.random() * ((length-1) - 0 + 1)) + 0
    return random
  }




